import React, { useState } from 'react'
import { Button } from 'react-bootstrap'

export default function Chatbot() {
    const [messages, setMessages] = useState([
        {
            message: 'Hi, May i have your name?'
        }
    ])
    return (
        <div>
            <div className="d-flex align-items-center justify-content-center">
                <img
                    src="https://www.cio.com/wp-content/uploads/2023/08/chatbot_ai_machine-learning_emerging-tech-100778305-orig.jpg?quality=50&strip=all"
                    alt="logo"
                    height={150}
                    width={200}
                />
                <h2 className='text-primary'>Chatbot</h2>
            </div>
            <div className="chat-message">
                {messages.length > 0 && messages.map((data) => <div>data</div>)}
                <div className="d-flex mt-2">
                    <input type="text" className="from-control"/>
                    <Button type="primary" classNmae="ms-3">Send</Button>
                </div>
                <div id='copyright' className="mt-3">Copyrights reserverd Tamil Skillhub</div>
            </div>
        </div>
    );
}
