import React from 'react'
import { Person } from 'react-bootstrap-icons'

export default function ChatMessage(props) {
    return (
        <div className='d-flex'>
            {
                props.user ? (
                    <span>
                        <span>{props.message}</span>
                        <i className='person' />
                        <Person />
                    </span>
                ) :  (
                    <span>
                        <Chat />
                        <i className='chat-dot' />
                        <span>{props.message}</span>
                    </span>
           )}
        </div>
    )
}
