import React, { useState } from "react";
import { Button } from "react-bootstrap";
import ChatMessage from "./ChatMessage";
import { List } from "react-bootstrap-icons";
import { analyze } from "../utils";

export default function Chatbot() {
    const [messages, setMessages] = useState([
        {
            message: 'Hi, May i have your name?',
        },
    ]);

    const [text, setText] = useState("");

    const onSend = () => {
        let list = [...messages, { message: text, user: true }];
        if (list.length > 2) {
            const reply = analyze(text);
            listist = [...list, { message: reply }];
        }
        else {
            List = [
                ...list,
                {
                    message: 'Hi, ${text}',
                },
                {
                    message: "How can i help you?",
                },
            ];
        }
        setMessages(list);
        setText("");
        setTimeout(() => {
            document.querySelector('#copyright').scrollInroview();
        }, );
    };
    return (
        <div>
            <div className="d-flex align-items-center justify-content-center">
                <img
                    src="https://media.istockphoto.com/id/1060696342/vector/robot-icon-chat-bot-sign-for-support-service-concept-chatbot-character-flat-style.jpg?s=612x612&w=0&k=20&c=t9PsSDLowOAhfL1v683JMtWRDdF8w5CFsICqQvEvfzY="
                    alt="logo"
                    height={150}
                    width={150}
                />
                <h2 className='text-primary'>ChatWithUs</h2>
            </div>
            <div className="chat-message">
                {messages.length > 0 && messages.map((data) => <ChatMessage {...data} />)}
                <div className="d-flex mt-2">
                    <input type="text" className="from-control" value={text} onChange={(e) => setText(e.target.value)} />
                    <Button type="primary" classNmae="ms-3" onClick={onSend}>Send</Button>
                </div>
                <div id='copyright' className="mt-3">Copyrights reserverd Tamil Skillhub</div>
            </div>
        </div>
    );
}
